package com.boss.homeworkout;import android.app.Notification;import android.app.NotificationChannel;import android.app.NotificationManager;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.os.Build;import android.os.Vibrator;import com.boss.homeworkout.Database.WorkoutDB;import static android.content.Context.NOTIFICATION_SERVICE;public class AlarmNotificationReceiver extends BroadcastReceiver {    WorkoutDB workoutDB;    @Override    public void onReceive(Context context, Intent intent) {        workoutDB = new WorkoutDB(context);        NotificationManager manager = (NotificationManager)context.getSystemService(NOTIFICATION_SERVICE);        if(Build.VERSION.SDK_INT >= 26){            String id = "channel_1";            String description = "143";            int importance = NotificationManager.IMPORTANCE_LOW;            NotificationChannel channel = new NotificationChannel(id, description, importance);            manager.createNotificationChannel(channel);            Notification notification = new Notification.Builder(context, id)                    .setContentTitle("GET UP")                    .setContentText("Time to exercise :)")                    .setSmallIcon(R.mipmap.ic_launcher)                    .setAutoCancel(true).build();            manager.notify(1, notification);            workoutDB.updateAlarmHour();            workoutDB.updateAlarmMinute();        }    }}